import React , { useState , useEffect } from 'react';
import './Zoo.css';

function Zoo () {
	
	const api = "https://zoo-animal-api.herokuapp.com/animals/rand/10";
	const [ animales , setAnimales ] = useState();
	
	useEffect(()=>{
		fetch( api )
		.then( zoo => zoo.json() )
		.then( zoo => {
		
			var todo = [];
			
			for ( var count = 0; count <= zoo.length-1; count++ ) {
				var animal = {
					"id" : zoo[ count ].id ,
					"nombre" : zoo[ count ].name ,
					"latin" : zoo[ count ].latin_name ,
				};
				todo.push({ 
				"id" : animal.id , 
				"posicion" : count , 
				"nombre" : animal.nombre , 
				"latin" : animal.latin ,
				});
			};
			
			setAnimales( todo );
			console.log( todo );
			
		});
	},[]);
	
	return (
		<section className="zoo">
			<h2>Zoo</h2>
			<div className="animales">
				{
					animales?.map((ani) => {
						return (
							<div className="animal" key={ ani.posicion }>
								<p>{ ani.nombre }<br/>({ ani.latin })</p>
							</div>
						);
					})
				}
			</div>
		</section>
	);
};


export default Zoo;
