import React, { useState , useEffect } from 'react';
import { useLocation, useNavigate } from "react-router-dom";

// COMPONENTS
import Button from "../components/Button";
import HelmetResumen from "../components/Helmet/HelmetResumen";
import Loading from "../components/Loading/Loading";

// CSS
import "../assets/css/RickAndMortyDetalle.css";

function RickAndMortyDetalle() {

	var location = useLocation();
	let navigate = useNavigate();
	
	
	
	// HABILITAR DESPUES DE TRABAJAR
	// const slug = location.pathname.split("/rickandmorty/")[1];
	
	
	
	
	const [traeData, setTraeData] = useState();
	const [ loading , setLoading ] = useState( false );
	
	useEffect(() => {
		
		setLoading(true);
		// TEMPORAL
		let max = 826;
		let min = 1;
		const slug = Math.floor(Math.random() * (max - min + 1)) + min;
		console.log( `slug: ${slug}` );
		const api = `https://rickandmortyapi.com/api/character/${slug}`;
		fetch(api).then(api => api.json())
			.then(perso => {
				var personaje = {
					"id": perso.id,
					"nombre": perso.name,
					"imagen": perso.image,
					"estado": function () {
						let estado = perso.status;
						if ( estado === "unknown" ) {
							estado = "Desconocido";
						} else if ( estado === "Alive" ) {
							estado = "Vivo";
						} else if ( estado === "Dead" ) {
							estado = "Muerto";
						} else if ( estado === "AAAAAAAA" ) {
						} else {
							alert(`Desconocido: ${estado}`);
						}
						return estado;
					} ,
					"especie": function () {
						let especie = perso.species;
						// especie = especie.replaceAll("Mythological Creature", "Criatura Mitol贸gica");
						// especie = especie.replaceAll("Alien", "Extraterrestre");
						especie = especie.replaceAll("Human", "Humana");
						especie = especie.replaceAll("Humanaoid", "Humanoide");
						especie = especie.replaceAll("Disease", "Enfermedad");
						especie = especie.replaceAll("AAAAAA", "EEEEEEE");
						especie = especie.replaceAll("AAAAAA", "EEEEEEE");
						especie = especie.replaceAll("AAAAAA", "EEEEEEE");
						
						if ( estado === "Mythological Creature" ) {
							estado = "Criatura Mitol贸gica";
						} else if ( estado === "Alien" ) {
							estado = "Extraterrestre";
						} else if ( estado === "Dead" ) {
							estado = "Muerto";
						} else if ( estado === "AAAAAAAA" ) {
							estado = "EEEEEEEEEE";
						} else {
							alert(`Desconocido: ${estado}`);
						}
						
						
						
						
						
						
						
						return especie;
					} ,
				};
				var todo = [];
				todo.push({
					id: personaje.id ,
					nombre: personaje.nombre ,
					imagen: personaje.imagen ,
					estado: personaje.estado() ,
					especie: personaje.especie()
				});
				setTraeData(todo);
				setLoading(false);
			});
			
	},[ setTraeData ]);

	if ( loading ) {
		return (
			<section id="RickAndMortyDetalle">
			<HelmetResumen title="Rick And Morty - Cargando ..." />
				<h2>Detalle</h2>
				<Loading />
			</section>
		);
	};

	return (
		<section id="RickAndMortyDetalle">
			<HelmetResumen title="Rick And Morty - Detalle" />
			<h2>Detalle</h2>
			{
				traeData?.map((elperso) => {
					return (
						<div className="detalle" key={elperso.id}>
							<img src={elperso.imagen} alt={ elperso.nombre } />
							<h3>{ elperso.nombre }</h3>
							<p>Estado: { elperso.estado }</p>
							<p>Especie: { elperso.especie }</p>
						</div>
					)
				})
			}
			<Button laclase="primary" onClick={() => navigate(-1)} texto="Volver" />
		</section>
	)
}

export default RickAndMortyDetalle;
