import React, { useEffect } from "react";

// COMPONENTS
// import ClimaEspecifico from "screens/clima/ClimaEspecifico";
import Icono from "components/Icono/Icono";
import { useState } from "react";

function Clima() {

	const [ clima , setClima ] = useState();

	useEffect(() => {
		var locacion = "sanestebanvalparaiso"
		var api = `https://weatherdbi.herokuapp.com/data/weather/${ locacion }`;
		fetch( api ).then( api => api.json() )
			.then( clima => {
				console.log( clima );
				setClima( clima )
			})
	},[])
	
	function ClimaEspecifico() {
		
		return (
			<>
				{
					clima?.map((cli) => {
						return (
							<div className="clima">
								<h4>{cli.region}</h4>
							</div>
						)
					})
				}
			</>
		)
	};

	return (
		<section id="clima">
			<h2>
				<Icono nombre="flood" />
				Clima
			</h2>
			<ClimaEspecifico />
		</section>
	)
}

export default Clima;
