import React, { useEffect } from "react";

// COMPONENTS
// import ClimaEspecifico from "screens/clima/ClimaEspecifico";
import Icono from "components/Icono/Icono";
import { useState } from "react";

function Clima() {

	const [ clima , setClima ] = useState();

	useEffect(() => {
		var locacion = "san esteban valparaiso"
		var api = `https://weatherdbi.herokuapp.com/data/weather/${ locacion }`;
		fetch( api ).then( api => api.json() )
			.then( clima => {
				console.log( clima );
				var objClima = {
					"locacion" : clima.region ,
					"actual" : {
						"diahora" : clima.currentConditions.dayhour ,
					}
				};
				var todo = [];
				todo.push({ "locacion" : objClima.locacion });
				setClima( todo )
			})
	},[])
	
	function ClimaEspecifico() {
		
		return (
			<>
				{
					clima?.map((cli) => {
						return (
							<div className="clima" key={cli.locacion}>
								<h4>{cli.locacion}</h4>
							</div>
						)
					})
				}
			</>
		)
	};

	return (
		<section id="clima">
			<h2>
				<Icono nombre="flood" />
				Clima
			</h2>
			<ClimaEspecifico />
		</section>
	)
}

export default Clima;
