import React , { useEffect , useState } from "react";
import Loading from './Loading';

function Test () {

	const [ loading , setLoading ] = useState( false );
	const [ elLibro , setElLibro ] = useState([]);

	useEffect(( libro ) => {
		
		setLoading( true );
	
		var nombre_libro = "porota";
		var nombre_libro = "papelucho";
		const api = `https://www.googleapis.com/books/v1/volumes?q=${ nombre_libro }`;
		
		fetch( api )
		.then( libro => libro.json() )
		.then( libro => {
			// console.log( libro );
			
			var todo = [];
			
			for ( var count = 0; count <= libro.items.length-1; count++ ) {
				
				var resumen = libro.items[ count ];
				
				todo.push({ 
					"autor" : libro.items[ count ].volumeInfo.authors , 
					"categoria" : libro.items[ count ].volumeInfo.categories , 
					"descripcion" : libro.items[ count ].volumeInfo.description , 
					"id" : libro.items[ count ].id , 
					"imagen" : libro.items[ count ].volumeInfo?.imageLinks ? libro.items[ count ].volumeInfo.imageLinks.thumbnail : "https://ouch-cdn2.icons8.com/DP-WOV6CKiyzfKpw4jgLyhW1t0lCk3ca-WuruEH9ZHY/rs:fit:455:456/czM6Ly9pY29uczgu/b3VjaC1wcm9kLmFz/c2V0cy9zdmcvMjU1/LzM3OGRkMjBjLWQ5/OGMtNGE3ZS1hYTQx/LWY3NDg0NjQwYzk2/Mi5zdmc.png" , 
					"nombre" : libro.items[ count ].volumeInfo.title , 
					"editorial" : resumen.volumeInfo.publisher ,
					"editorial_test" : String(resumen.volumeInfo.publisher).length <= 2 ? libro.items[ count ].volumeInfo.publisher : "No especificada" ,
					"editorial_cantidad" : String(resumen.volumeInfo.publisher).length ,
				});

				console.log( resumen.volumeInfo.publisher );

			};
			
			setElLibro( todo );
			setLoading( false );

		});
		
	},[]);
	
	if ( loading ) {
		return (
			<section className="test">
				<h2>Libros</h2>
				<Loading />
			</section>
		);
	};

	return (
		<section className="test">
			<h2>Libros</h2>
			<div className="libros">
				{
					elLibro.map( ( librito ) => {
						return (
								<div className="libro" key={ librito.id }>
									{/* <img style={{ width: '128px' }} src={ librito.imagen } alt={ librito.nombre } /> */}
									<img style={{ width: '100px' }} src={ librito.imagen } alt={ librito.nombre } />
									<ul>
										<li>Libro: { librito.nombre }</li>
										<li>Autor: { librito.autor }</li>
										<br/>
										<br/>
										<li>Editorial: { librito.editorial } ({ librito.editorial_cantidad })</li>
										<li>Editorial Test: { librito.editorial_test } ({ librito.editorial_cantidad })</li>
										<br/>
										<br/>
										<li>Categoria: { librito.categoria }</li>
									</ul>
									<p>{ librito.descripcion }</p>
								</div>
						);
					})
				}
			</div>
			
			
			
		</section>
	);
};

export default Test;
