import $ from "jquery";
import axios from "axios";

const traePaises = async (state) => {
	const peticion = await axios.get( `http://universities.hipolabs.com/search?country=chile` );
	state( peticion.data );
};

const traePaisesReloaded = async ( pais , state ) => {
	const peticion = await axios.get( `http://universities.hipolabs.com/search?country=${pais}` );
	console.log( peticion.data );
};

const buscarPais = () => {
	var buscando = $( ".buscador > input" ).val()
	console.log( `Buscar el pais de: ${buscando}` );
	// traePaisesReloaded()
}


// http://universities.hipolabs.com/search?country=${pais}

export { 
	traePaises , 
	buscarPais , 
	traePaisesReloaded
};
