import React from 'react'

class BackgroundColor extends React.Component {
	state = { 
		color : 'red'
	}
	
	changeColor=()=>{
		let color = this.state.color === 'red' ? 'green' : 'red'
		this.setState({color})
	}
	
	
	render(){
		return (
			<div>
			<div style= {{backgroundColor : this.state.color}}></div>
			<button onClick={this.changeColor}></button>
			</div>
		)
	}
		
}
	
	
