import axios from "axios";

const traePaises = async (state) => {
	// const peticion = await axios.get( `http://universities.hipolabs.com/search?country=${ pais }` );
	const peticion = await axios.get( `http://universities.hipolabs.com/search?country=chile` );
	// console.log( peticion.data[0] );
	state( peticion.data );
};

const traePaisesReloaded = async (state) => {
	// const peticion = await axios.get( `http://universities.hipolabs.com/search?country=${ pais }` );
	const peticion = await axios.get( `http://universities.hipolabs.com/search?country=chile` );
	// console.log( peticion.data[0] );
	state( peticion.data );
};

const buscarPais = async (nombrepais) => {
	console.log( `Buscar el pais de: ${nombrepais}` );
}

// http://universities.hipolabs.com/search?country=${pais}

export { 
	traePaises , 
	buscarPais , 
	traePaisesReloaded
};
