import React , {useEffect , useState} from 'react';
import HelmetResumen from '../components/Helmet/HelmetResumen';
// import $ from "jquery";

import Loading from '../components/Loading/Loading';
import Button from "../components/Button";

import { traePaises , buscarPais } from "../helpers/functions";

import "../assets/css/Universidades.css";

function Universidades () {
	
	const [ loading , setLoading ] = useState( false );
	const [universidades, setUniversidades] = useState("argentina");
	// const [universidadesReloaded, setUniversidadesReloaded] = useState(null);
	// const [ u_nombre , setU_nombre ] = useState();
	// const [imprimeAlphaCode, setImprimeAlphaCode] = useState();
	// const [exportarBuscador, setExportarBuscador] = useState();
	
	buscarPais( "argentina" )
	
	useEffect(() => {
		
		setLoading( true );
		
		traePaises(setUniversidades);
		setLoading( false );
		
	},[]);
	
	function Universidades( props ) {
	
		traePaises( props.pais )
	
		return (
			<>
				{
					universidades?.map((uni) => {
						return(
							<div key={uni.name}>
								<h1>{uni.name}</h1>
							</div>
						)
					})
				}
			</>
		)
	};
	
	
	if ( loading ) {
		return (
			<section className="lasuniversidades">
				<HelmetResumen title="Cargando Universidades ..." />
				<h2>Universidades</h2>
				<Loading />
			</section>
		);
	};
	
	return ( 
		<section id="lasuniversidades">
		
			<HelmetResumen title="Universidades" />
			<h2>Universidades</h2>
			
			<div className="buscador">
				<input className="buscaPais" type="text" list="alphas" placeholder="Ingresa Pais" />
				<datalist id="alphas">
					<option value="peru" />
					<option value="argentina" />
				</datalist>
				<Button laclase="primary" texto="Buscar" />
			</div>
			
			<div className="universidades">
				<Universidades pais={universidades} />
			</div>
			
		</section>
	)
};

export default Universidades;
