import React , { useEffect } from 'react';

import './Footer.css';

function Footer () {

	useEffect(() => {

		let alto = {
			"html" : document.body.clientHeight ,
			"ventana" : document.documentElement.clientHeight ,
		}
		
		console.log( `HTML => ${ alto.html }` );
		console.log( `VENTANA => ${ alto.ventana }` );
		
		if ( alto.html <= alto.ventana ) {
			console.log( `HTML es menor que VENTANA` );
			document.querySelector( ".footersito" ).classList.add("fix");
			// document.querySelector(".footersito").innerHTML = "asdas";
			// element.classList.add("fix");
		} else {
			console.log( `HTML es mayor que VENTANA` );
		};
		
	},[]);

	return (
		<footer className="footersito">
			<p>Derechos no reservados</p>
		</footer>
	);
};

export default Footer;
