import React , { useState } from 'react';
import Button from "components/Button";
import $ from "jquery";

const Props = () => {
	const [cambioTexto, setCambioTexto] = useState(null);
	
	function pulsarTecla() {
		var algo = $("input").val();
		setCambioTexto(algo)
	}

	function Componentito( props ) {
		return <h1 style={{ padding: "0 60px" , backgroundColor: "red" , color: "white" , margin: "0 auto" , textAlign: "center" , fontSize: "6em" }}>{props.texto}</h1>
	}
	
	const Maps = ( props ) => {
		
		// const API_KEY = "AIzaSyBvq7mT_Xbr94PMdJRLVMjHPdR253oSRfo";
		// Initialize and add the map
function initMap() {
	// The location of Uluru
	const uluru = { lat: -25.344, lng: 131.031 };
	// The map, centered at Uluru
	const map = new google.maps.Map(document.getElementById("map"), {
	  zoom: 4,
	  center: uluru,
	});
	// The marker, positioned at Uluru
	const marker = new google.maps.Marker({
	  position: uluru,
	  map: map,
	});
  }
  
  window.initMap = initMap;
		
		return(
			<div className="gmaps">
				<iframe 
					// https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d13415.269838936052!2d-70.5859866!3d-32.79709905!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1ses-419!2scl!4v1660451543878!5m2!1ses-419!2scl
					src={`https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d13415.269838936052!2d${props.longitud}!3d${props.latitud}05!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1ses-419!2scl!4v1660451543878!5m2!1ses-419!2scl`}
					// src={`https://www.google.com/maps/embed/v1/view?zoom=17&center=-32.8014,-70.5872&key=${API_KEY}`}
					// src={`https://www.google.com/maps/embed/v1/directions?key=${API_KEY}&zoom=17&center=-32.8014,-70.5872`}
					width="600"
					height="450"
					style={{ border: 0 }}
					title="Maps"
					>
				</iframe>
			</div>
		)
	};

	return(
		<>
		<section id="componentest">
			<Maps latitud="-32.80149" longitud="-70.58718" />
			<style type="text/css">{`
				.red {
					color: red
				}
				.green {
					color: green
				}
				
			`}
			</style>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
	
			<br></br>
			<h1>ComponenTEST</h1>
			<div style={{ display: "flex" , justifyContent: "center" , flexDirection: "column" , alignItems: "center" , width: "250px" , margin: "0 auto 40px" }}>
				<input className="elinput" onChange={pulsarTecla} style={{ width: "100%" , height: "45px" , padding: "6px" , marginBottom: "10px" }} />
				<Button laclase="warning" texto="Cambiar Texto" />
				<Button />
			</div>
			<Componentito texto={cambioTexto} />
		</section>
		</>
	)
}

export default Props;
