import React , { useEffect , useState } from "react";
import { NavLink , useLocation } from "react-router-dom";
import HelmetResumen from "components/Helmet/HelmetResumen";

// COMPONENTS
import Icono from 'components/Icono/Icono';
import BuscadorLibros from 'components/Libros/BuscadorLibros';
import Loading from 'components/Loading/Loading';
import Button from 'components/Button';

import 'components/Libros/Libros.css';

// https://www.googleapis.com/books/v1/volumes?q=${ book }

function Libros () {

	const [ loading , setLoading ] = useState( false );
	const [ buscador , setBuscador ] = useState( false );
	const [ elLibro , setElLibro ] = useState([]);
	
	
	var location = useLocation();

	var elSlug = location.pathname.split( "/libro/" )[1];
	
	
	useEffect(() => {

		setLoading( true );
		
		var book = location.pathname.split( "/libro/" )[1];
		if ( book === undefined || book.length <= 0 ) {
			setLoading( false );
			setBuscador( true )
		} else {
			service_GetDataLibro( book )
		};
		
	},[location]);
	
	if ( loading ) {
		return (
			<section className="libros">
				<HelmetResumen title="Buscador de Libros" />
				<h2>
					<Icono nombre="menu_book" />
					Libros
				</h2>
				<Loading />
			</section>
		);
	};
	
	return (
		<section className="libros">
			<HelmetResumen title="Buscador de Libros" />
			<h2>
				<Icono nombre="menu_book" />
				Libros
			</h2>
			<BuscadorLibros />
		</section>
	);











