import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

// COMPONENTS
import Button from "../components/Button";
import HelmetResumen from "../components/HelmetResumen";

import "../components/CryptoDetalle.css";

// DOCUMENTACION
// https://www.coingecko.com/es/api/documentation

function CryptoDetalle() {
	var location = useLocation();
	let navigate = useNavigate();
	const cryptoSlug = location.pathname.split("/crypto/")[1];
	//const api_detalle = `https://api.coincap.io/v2/assets/${cryptoSlug}`;
	//const api_detalle = `https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc`;
	const api_detalle = `https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=250&page=1&sparkline=true&price_change_percentage=24h`;
	const [cryptoState, setCryptoState] = useState();
	const [algo , setAlgo] = useState({});
	
	useEffect(() => {
		fetch(api_detalle)
		.then((api_detalle) => api_detalle.json())
		.then((detalleData) => {
			var resumen = detalleData[cryptoSlug];
			var detalle = {
				id: cryptoSlug,
				imagen: resumen.image,
				nombre: resumen.name,
				abreviatura: resumen.symbol,
				rango24h: {
					max: resumen.high_24h,
					min: resumen.high_24h
				},
				volumen: resumen.total_volume,
				ranking: resumen.market_cap_rank,
				precio: {
					actual: () => {
						let precio_actual = resumen.current_price;
						precio_actual = Number(precio_actual).toLocaleString();
						return precio_actual;
					},
					anterior: function () {
						let precio_exacto = resumen.sparkline_in_7d.price;
						precio_exacto = precio_exacto.at(-1);
						precio_exacto = Number(precio_exacto.toFixed(0)).toLocaleString();
						return precio_exacto;
					}
				},
				status: () => {
					let status = "AAAAAAAAAAAAaaaaa";
					let precio_actual = detalle.precio.actual();
					let precio_anterior = detalle.precio.anterior();
					if (precio_anterior === precio_actual) {
						status = "ESTA IGUAL";
					} else if (precio_anterior > precio_actual) {
						status = "Bajo el precio uwu";
					} else {
						status = "Subio :D";
					}
					
					return status;
				}
			};
			
			var todo = [];
			todo.push({
				id: detalle.id,
				imagen: detalle.imagen,
				nombre: detalle.nombre,
				abreviatura: detalle.abreviatura,
				rango24h: {
					max: detalle.rango24h.max,
					min: detalle.rango24h.max
				},
				volumen: detalle.volumen,
				ranking: detalle.ranking,
				precio: {
					actual: detalle.precio.actual(),
					anterior: detalle.precio.anterior()
				},
				status: detalle.status()
			});
			
			setCryptoState(todo);
			
			
			
			
			
			
			
			
			
			
			
			
			
			
			
			
			var algotest = {
				nombre: "Pauloso",
				edad: 33
			}
			var test = [];
			test.push({
				nombre: algotest.nombre,
				edad: algotest.edad
			});
			// console.log( `asds ${test[0].nombre}` );
			// setAlgo(algotest);
			setAlgo(test);
			
		});
	// }, [api_detalle, cryptoSlug , algo,setAlgo]);
	}, [algo,api_detalle,cryptoSlug]);
	
	return (
		<section id="cryptodetalle">
			{/* <HelmetResumen title={`C 1: ${setCryptoState({ cryptoState: this.state.nombre})}`} /> */}
			{/* <HelmetResumen title={`C1: ${algo.test[0].nombre}`} /> */}
			<h1>TEST: {`asdasd ${algo.nombre}`}</h1>
			
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			<br></br>
			
			
			{cryptoState?.map((cryp) => {
				return (
					<div className="detalle">
					<h1>
					<strong>
					{cryp.nombre} ({cryp.abreviatura.toUpperCase()})
					</strong>
					<span className="status">Status: {cryp.status}</span>
					</h1>
					<p className="ranking">
					Rank: <strong>#{cryp.ranking}</strong>
					</p>
					<div className="imagen">
					<img src={cryp.imagen} alt={cryp.nombre} />
					</div>
					<h2>
					<strong>
					Ahora: $ {cryp.precio.actual} Antes: $ {cryp.precio.anterior}
					</strong>
					</h2>
					<p>Volumen: {cryp.volumen.toLocaleString()}</p>
					<p>Rango 24 hrs</p>
					<ul>
					<li>Max: $ {cryp.rango24h.max.toLocaleString()}</li>
					<li>Min: $ {cryp.rango24h.min.toLocaleString()}</li>
					</ul>
					</div>
					);
				})}
				<Button onClick={() => navigate(-1)} texto="Volver" />
				</section>
				);
			}
			
			export default CryptoDetalle;
