import React , { useEffect, useState } from 'react';
import Button from "../components/Button";
import $ from "jquery";

import {pulsar} from "../helpers/onclick";

function Props() {
	
	const [cambioTexto, setCambioTexto] = useState("algo");
	
	
	useEffect(() => {
		// setCambioTexto( algo )
		// console.log( "a" );
