import React, { useState , useEffect } from 'react';
import { useLocation, useNavigate } from "react-router-dom";

// COMPONENTS
import Button from "../components/Button";
import HelmetResumen from "../components/Helmet/HelmetResumen";
import Loading from "../components/Loading/Loading";

// CSS
import "../assets/css/RickAndMortyDetalle.css";

function RickAndMortyDetalle() {

	var location = useLocation();
	let navigate = useNavigate();
	
	
	
	// HABILITAR DESPUES DE TRABAJAR
	// const slug = location.pathname.split("/rickandmorty/")[1];
	
	
	
	
	const [traeData, setTraeData] = useState();
	const [ loading , setLoading ] = useState( false );
	
	useEffect(() => {
		
		setLoading(true);
		// TEMPORAL
		let max = 826;
		let min = 1;
		const slug = Math.floor(Math.random() * (max - min + 1)) + min;
		console.log( `slug: ${slug}` );
		const api = `https://rickandmortyapi.com/api/character/${slug}`;
		fetch(api).then(api => api.json())
			.then(perso => {
				var personaje = {
					"id": perso.id,
					"nombre": perso.name,
					"imagen": perso.image,
					"estado": function () {
						let estado = perso.status;
						estado = estado.replaceAll("unknown", "desconocido");
						estado = estado.replaceAll("Alive", "vivo");
						estado = estado.replaceAll("Dead", "muerto");
						return estado;
					} ,
					"especie": function () {
						let especie = perso.species;
						especie = especie.replaceAll("AAA", "Criatura Mitologica");
						especie = especie.replaceAll("Human", "Humana");
						return especie;
					} ,
				};
				var todo = [];
				todo.push({
					id: personaje.id ,
					nombre: personaje.nombre ,
					imagen: personaje.imagen ,
					estado: personaje.estado() ,
					especie: personaje.especie()
				});
				setTraeData(todo);
				setLoading(false);
			});
			
	},[ setTraeData ]);

	if ( loading ) {
		return (
			<section id="RickAndMortyDetalle">
			<HelmetResumen title="Rick And Morty - Cargando ..." />
				<h2>Detalle</h2>
				<Loading />
			</section>
		);
	};

	return (
		<section id="RickAndMortyDetalle">
			<HelmetResumen title="Rick And Morty - Detalle" />
			<h2>Detalle</h2>
			{
				traeData?.map((elperso) => {
					return (
						<div className="detalle" key={elperso.id}>
							<img src={elperso.imagen} alt={ elperso.nombre } />
							<h3>{ elperso.nombre }</h3>
							<p>Estado: { elperso.estado }</p>
							<p>Especie: { elperso.especie }</p>
						</div>
					)
				})
			}
			<Button laclase="primary" onClick={() => navigate(-1)} texto="Volver" />
		</section>
	)
}

export default RickAndMortyDetalle;
