import React , {useEffect , useState} from 'react';

function Universidades () {
	
	// const [ u_nombre , setU_nombre ] = useState();
	
	useEffect(() => {
		const universidades = "http://universities.hipolabs.com/search?country=Chile";
		fetch( universidades )
		.then( x => x.json() )
		.then(x => {
			
			var todo = [];
			
			for ( var count = 0; count <= 20; count++ ) {
				var uni = {
					"nombre" : x[ count ].name ,
				}
				console.log( uni.nombre );
				setU_nombre( uni.nombre );
				todo.push({ "nombre" : uni.nombre })
			};
		})
	},[]);
	
	return ( 
		<section className="fetch">
			<h2>Universidades</h2>
			<div className="universidades">
				{/* <div className="universidad">
					<h4>{ u_nombre }</h4>
				</div> */}
			</div>
			{
				todo.map(( uni ) => {
					return(
						<div className="universidad">${ uni.nombre }</div>
					)
				})
			}
		</section>
	)
};

export default Universidades;
