import React, { useEffect , useState } from "react";
import { useLocation } from "react-router-dom";

function LibroDetalle () {
	
	const [ traeInfo , setTraeInfo ] = useState();
	
	const id = useLocation().pathname.split( "/detalle/" )[1];
	useEffect(() => {
		
		const api = `https://www.googleapis.com/books/v1/volumes/${ id }`;
		fetch( api )
		.then( detalle => detalle.json() )
		.then( detalle => {
			
			var todo = [];
			
			for ( var count = 0; count <= 0; count++ ) {
				var uni = {
					"id" : detalle.id ,
					// "titulo" : detalle.volumeInfo.title ,
					// "subtitulo" : detalle.volumeInfo.subtitle ,
					// "autor" : detalle.volumeInfo.authors ,
				}
				todo.push({ 
					"id" : uni.id , 
					// "titulo" : data.titulo , 
					// "subtitulo" : data.subtitulo , 
					// "autor" : data.autor ,
				});
			};

			
			
			console.log( todo );
			setTraeInfo( todo );
			// console.log( `Nombre: ${ ...data }` );
			
		})
		
	},[id]);

	return (
		<section className="detalleLibro">
			<h2>DETALLE LIBRO</h2>
				{
					traeInfo?.map( ( info ) => {
						return (
							<div className="detalle" key={ info.id }>
								{/* <p>{ info.titulo }</p> */}
							</div>
						);
					})
				}
		</section>
	);
}

export default LibroDetalle;
