import React, { useState , useEffect } from "react";
import { useLocation } from "react-router-dom";

function LibroDetalle () {
	
	const [ traeInfo , setTraeInfo ] = useState();
	
	var id = useLocation().pathname.split( "/detalle/" )[1];
	useEffect(() => {
		
		var api = `https://www.googleapis.com/books/v1/volumes/${ id }`;
		fetch( api )
		.then( detalle => detalle.json() )
		.then( detalle => {
			
			var todo = [];

			var data = {
				"id" : detalle.id ,
				// "titulo" : detalle.volumeInfo.title ,
				// "subtitulo" : detalle.volumeInfo.subtitle ,
				// "autor" : detalle.volumeInfo.authors ,
			}

			todo.push({ 
				"id" : data.id , 
				"titulo" : data.titulo , 
				"subtitulo" : data.subtitulo , 
				"autor" : data.autor ,
			});
			
			
			console.log( data.id );
			// setTraeInfo( todo );
			// console.log( `Nombre: ${ ...data }` );
			
		},[]);
		
	});

	return (
		<section className="detalleLibro">
			<h2>DETALLE LIBRO</h2>
				<div>
				{
					traeInfo?.map( ( info ) => {
						return (
							<div className="detalle" key={ info.id }>
								<p>{ info.titulo }</p>
							</div>
						);
					})
				}
				</div>
		</section>
	);
}

export default LibroDetalle;
