import React, { useState , useEffect } from 'react';
import { useLocation, useNavigate } from "react-router-dom";

// COMPONENTS
import Button from "../components/Button";
import HelmetResumen from "../components/Helmet/HelmetResumen";
import Loading from "../components/Loading/Loading";

// CSS
import "../assets/css/RickAndMortyDetalle.css";

function RickAndMortyDetalle() {

	var location = useLocation();
	let navigate = useNavigate();
	
	
	
	// HABILITAR DESPUES DE TRABAJAR
	// const slug = location.pathname.split("/rickandmorty/")[1];
	
	
	
	
	const [traeData, setTraeData] = useState();
	const [ loading , setLoading ] = useState( false );
	
	useEffect(() => {
		
		setLoading(true);
		// TEMPORAL
		let max = 826;
		let min = 1;
		const slug = Math.floor(Math.random() * (max - min + 1)) + min;
		// const api = `https://rickandmortyapi.com/api/character/${slug}`;
		// const api = `https://rickandmortyapi.com/api/character/719`;
		const api = `https://rickandmortyapi.com/api/character/487`;
		
		
		// const api = `https://rickandmortyapi.com/api/character/413`;
		// const api = `https://rickandmortyapi.com/api/character/470`;
		// const api = `https://rickandmortyapi.com/api/character/188`;
		// const api = `https://rickandmortyapi.com/api/character/262`;
		fetch(api).then(api => api.json())
			.then(perso => {
				var personaje = {
					"id": perso.id,
					"nombre": perso.name,
					"imagen": perso.image,
					"estado": function () {
						let estado = perso.status;
						if ( estado === "unknown" ) {
							estado = "Desconocido";
						} else if ( estado === "Alive" ) {
							estado = "Vivo";
						} else if ( estado === "Dead" ) {
							estado = "Muerto";
						} else if ( estado === "AAAAAAAA" ) {
						} else {
							alert(`Estado Desconocido: ${estado}`);
						}
						return estado;
					} ,
					"especie": function () {
						let especie = perso.species;
						if ( especie === "Mythological Creature" ) {
							especie = "Criatura Mitológica";
						} else if ( especie === "Alien" ) {
							especie = "Extraterrestre";
						} else if ( especie === "Human" ) {
							especie = "Humana";
						} else if ( especie === "Humanoid" ) {
							especie = "Humanoide";
						} else if ( especie === "Disease" ) {
							especie = "Enfermedad";
						} else if ( especie === "unknown" ) {
							especie = "Desconocida";
						} else if ( especie === "Robot" ) {
							especie = "Robot";
						} else if ( especie === "Animal" ) {
							especie = "Animal";
						} else if ( especie === "AAAAAAAA" ) {
							especie = "EEEEEEEEEE";
						} else {
							alert(`Especie desconocida: ${especie}`);
						}
						return especie;
					} ,
					"tipo" : function() {
						let tipo = perso.type;
						if (tipo === "") {
							// tipo = "AAAAAAAAAAAAAAAAAAAAAAAAAAA!!";
						} else if ( tipo === "Parasite" ) {
							tipo = "Parasito";
						} else if ( tipo === "Poopybutthole" ) {
							tipo = "Cara Culo";
						} else if ( tipo === "Crow" ) {
							tipo = "Cuervo";
						} else if ( tipo === "Changeformer" ) {
							tipo = "Transformer";
						} else if ( tipo === "Soulless Puppet" ) {
							tipo = "Marioneta sin Alma";
						} else if ( tipo === "Unknown-nippled alien" ) {
							tipo = "Extranjera de pezones desconocidos";
						} else if ( tipo === "Dog" ) {
							tipo = "Perro";
						} else if ( tipo === "Old Amazons" ) {
							tipo = "Amazona Vieja";
						} else if ( tipo === "Decoy" ) {
							tipo = "Señuelo";
						} else if ( tipo === "Plutonian" ) {
							tipo = "Plutoniano";
						} else if ( tipo === "Cyborg" ) {
							tipo = "Cyborg";
						} else if ( tipo === "Gazorpian" ) {
							tipo = "Gazorpiana";
						} else if ( tipo === "CHUD" ) {
							tipo = "CHUD";
						} else if ( tipo === "Weasel" ) {
							tipo = "Comadreja";
						} else if ( tipo === "AAAAAAAA" ) {
							tipo = "EEEEEEEEEE";
						} else if ( tipo === "AAAAAAAA" ) {
							tipo = "EEEEEEEEEE";
						} else if ( tipo === "AAAAAAAA" ) {
							tipo = "EEEEEEEEEE";
						} else {
							alert(`[${this.id}] Tipo desconocida: ${tipo}`);
						}
						if ( tipo.length === "" ) {
							console.log( `Tipo: NO LO TRAE EL JSON` );
						}
						return tipo;
					},
				};
				
				console.log( `[${personaje.id}] Tipo: ${personaje.tipo()}` );
				var todo = [];
				todo.push({
					id: personaje.id ,
					nombre: personaje.nombre ,
					imagen: personaje.imagen ,
					estado: personaje.estado() ,
					especie: personaje.especie() ,
					tipo: personaje.tipo ,
				});
				setTraeData(todo);
				setLoading(false);
			});
			
	},[ setTraeData ]);

	if ( loading ) {
		return (
			<section id="RickAndMortyDetalle">
			<HelmetResumen title="Rick And Morty - Cargando ..." />
				<h2>Detalle</h2>
				<Loading />
			</section>
		);
	};

	return (
		<section id="RickAndMortyDetalle">
			<HelmetResumen title="Rick And Morty - Detalle" />
			{
				traeData?.map((elperso) => {
					return (
						<div className="detalle" key={elperso.id}>
							<img src={elperso.imagen} alt={ elperso.nombre } />
							<h3>{ elperso.nombre }</h3>
							<p><strong>Estado: </strong>{ elperso.estado }</p>
							<p><strong>Especie: </strong>{ elperso.especie }</p>
							{
								// String( elperso.tipo ).length >= 1 ? <p><strong>Tipo: </strong>{ elperso.tipo }</p> : ""
								elperso.tipo === "" >= 1 ? <p><strong>Tipo: </strong>{ elperso.tipo() }</p> : "NO TIENE!"
							}
						</div>
					)
				})
			}
			<Button laclase="primary" onClick={() => navigate(-1)} texto="Volver" />
		</section>
	)
}

export default RickAndMortyDetalle;
