import React, { useState } from "react";

export default function App() {
	const [cls, setCls] = useState("green");
	
	return (
		<>
		<style type="text/css">{`
			.red {color: red}
			.green {color: green}
		`}
		</style>
		<button className={cls} onClick={() => setCls((cls) => (cls === "red" ? "green" : "red"))} > Button </button>
		</>
	);
}
	
	
	
