import React, { useState, useEffect } from "react";
import $ from "jquery";

// COMPONENTS
import Button from "../components/Button";

// CSS
import "../components/Feriados.css";

// https://date.nager.at/
// https://date.nager.at/api/v3/PublicHolidays/2017/AT

function Feriados() {
  const [feriados, setFeriados] = useState();

  const api = "https://date.nager.at/api/v3/PublicHolidays/2022/CL";

  //COMPONENTE BUSCADOR
  function Buscador() {
    function elbuscar() {
      let lobuscado = $("#buscador input").val();
      $.each($(".feriados .feriado"), function () {
        if ($(this).attr("data-feriadonombre").includes(lobuscado)) {
          $(this).show();
        } else {
          $(this).hide();
        }
      });
    }
    return (
      <div id="buscador">
        <input
          className="inputBuscar"
          type="text"
          placeholder="Buscar por Pais"
        />
        <Button laclase="primary" />
      </div>
    );
  }

  useEffect(() => {
    fetch(api)
      .then((api) => api.json())
      .then((feriados) => {
        var todo = [];
        for (var count = 0; count <= feriados.length - 1; count++) {
          var resumen = feriados[count];
          var feriado = {
            id: resumen.date,
            nuevoid: function () {
              let nuevoid = count + 1;
              return nuevoid;
            },
            nombre: function () {
              let nombre = resumen.localName;
              nombre = nombre.toLowerCase();
              return nombre;
            },
            fecha: resumen.date
          };
          todo.push({
            id: feriado.id,
            nuevoid: feriado.nuevoid(),
            nombre: feriado.nombre(),
            fecha: feriado.fecha
          });
        }
        setFeriados(todo);
      });
  }, [setFeriados]);

  return (
    <section id="feriados">
      <h2>FERIADOS</h2>
      <Buscador />
      <div className="feriados">
        {feriados?.map((feriado) => {
          return (
            <div
              className="feriado"
              data-feriadonombre={feriado.nombre}
              key={feriado.nuevoid}
            >
              <h4>
                <strong>{feriado.nombre}</strong>
              </h4>
              <p>{feriado.fecha}</p>
            </div>
          );
        })}
      </div>
    </section>
  );
}

export default Feriados;
