import React , {useEffect , useState} from 'react';
import HelmetResumen from './HelmetResumen';
import Loading from './Loading';

function FetchChow () {
	
	const traeImagen = "https://dog.ceo/api/breeds/image/random";

	const [ loading , setLoading ] = useState( false );
	const [ imagen , setImagen ] = useState(traeImagen);
	const [ raza , setRaza ] = useState();
	
	useEffect(() => {
	
		setLoading( true );
		
		fetch( traeImagen )
		.then( x => x.json() )
		.then(x => {
		
			var perrito = x.message;
			var raza    = perrito.split( "/breeds/" )[1];
			raza = raza.split( "/" )[0];
			raza = raza.replaceAll( "-" , " " );
			raza = raza.toUpperCase();
			
			console.log( `Perrito: ${perrito}` );
			console.log( `Raza: ${raza}` );

			setImagen(perrito);
			setRaza( raza );
			setLoading( false );
			
		});
		
	},[]);
	
	if ( loading ) {
		return (
			<section className="fetch">
				<h2>Perrito Random</h2>
				<Loading />
			</section>
		);
	};
	
	return ( 
		<section className="fetch">
			<HelmetResumen title="" />
			<h2>Perrito Random</h2>
			<img width="500" src={imagen} alt="GUAU!" />
			<p>{raza}</p>
		</section>
	)
	
};

export default FetchChow;
