import React , { useEffect , useState } from 'react';

function Usuario () {
	
	const [ usuario , setUsuario ] = useState();
	
	useEffect(() => {
		fetch( "https://randomuser.me/api/" )
		.then( x => x.json() )
		.then( x => {
			var getUsuario = [{
				"seed"          : x.info.seed ,
				"genero"        : x.results[ 0 ].gender ,
				"nombre"        : {
					"title" : x.results[ 0 ].name.title ,
					"nombre" : x.results[ 0 ].name.first ,
					"apellido" : x.results[ 0 ].name.last ,
				} ,
				"direccion"     : {
					"calle" : {
						"numero" : x.results[ 0 ].location.street.number ,
						"nombre" : x.results[ 0 ].location.street.name ,
					} ,
					"ciudad" : x.results[ 0 ].location.city ,
					"estado" : x.results[ 0 ].location.state ,
					"pais" : x.results[ 0 ].location.country ,
					"codigopostal" : x.results[ 0 ].location.postcode ,
					"cordenadas" : {
						"latitud" : x.results[ 0 ].location.coordinates.latitude ,
						"longitude" : x.results[ 0 ].location.coordinates.longitud ,
					} ,
				} ,
				"celular"       : x.results[ 0 ].cell ,
				"edad"          : x.results[ 0 ].dob.age ,
				"imagen_large"  : x.results[ 0 ].picture.large ,
			}];
			
			console.log( getUsuario );
			
			setUsuario( getUsuario );
			
		});
	},[]);

	return ( 
		<section className="usuario">
			<h2>Usuario</h2>
			<div className="elusuario">
				{
					usuario?.map((elusuario) => {
						return(
							<div key={elusuario.seed}>
								<img src={ elusuario.imagen_large } alt={ elusuario.nombre.nombre } />
								<p>{ elusuario.nombre.title }. { elusuario.nombre.nombre } { elusuario.nombre.apellido }</p>
								<ul>
									<li>Edad: { elusuario.edad }</li>
									<li>Genero: { elusuario.genero }</li>
									<li>Celular: <a href={`tel:${ elusuario.celular }`}>{ elusuario.celular }</a></li>
								</ul>
								<p>Direccion:</p>
								<ul>
									<li>Calle: { elusuario.direccion.calle.nombre }, { elusuario.direccion.calle.numero }</li>
									<li>Ciudad: { elusuario.direccion.ciudad }</li>
									<li>Estado: { elusuario.direccion.estado }</li>
									<li>Pais: { elusuario.direccion.pais }</li>
									<li>Codigo Postal: { elusuario.direccion.codigopostal }</li>
									<li>Cordenadas: { elusuario.direccion.cordenadas.latitud } - { elusuario.direccion.cordenadas.longitud }</li>
								</ul>
							</div>
						);
					})
				}
			</div>
		</section>
	);
};

export default Usuario;
