import React , { useEffect , useState } from "react";
import Loading from './Loading';

function Test () {

	const [ loading , setLoading ] = useState( false );
	const [ elLibro , setElLibro ] = useState([]);

	useEffect(( libro ) => {
		
		setLoading( true );
	
		var nombre_libro = "porota"
		const api = `https://www.googleapis.com/books/v1/volumes?q=${ nombre_libro }`;
		
		fetch( api )
		.then( libro => libro.json() )
		.then( libro => {
			// console.log( libro );
			
			var todo = [];
			
			for ( var count = 0; count <= libro.items.length-1; count++ ) {
				// var getLibro = {
				// 	"id" : libro.items[ count ].id ,
				// 	"nombre" : libro.items[ count ].volumeInfo.title ,
				// 	"autor" : libro.items[ count ].volumeInfo.authors ,
				// };

				// todo.push({ 
				// 	"nombre" : getLibro.nombre , 
				// 	"id" : getLibro.id , 
				// 	"autor" : getLibro.autor , 
				// });
				
				console.log( libro.items[ count ].volumeInfo );
				console.log( `Imagen: ${ libro.items[ count ].volumeInfo }` );
				
				todo.push({ 
					"nombre" : libro.items[ count ].volumeInfo.title , 
					"id" : libro.items[ count ].id , 
					"autor" : libro.items[ count ].volumeInfo.authors , 
					"categoria" : libro.items[ count ].volumeInfo.categories , 
					"descripcion" : libro.items[ count ].volumeInfo.description , 
					"imagen" : libro.items[ count ].volumeInfo?.imageLinks[0] , 
				});

			};
			
			setElLibro( todo );
			setLoading( false );

		});
		
	},[]);
	
	if ( loading ) {
		return (
			<section className="test">
				<h2>Libros</h2>
				<Loading />
			</section>
		);
	};

	return (
		<section className="test">
			<h2>Libros</h2>
			<div className="libros">
				{
					elLibro.map( ( librito ) => {
						return (
								<div className="libro" key={ librito.id }>
									<img src={ librito.imagen } alt={ librito.nombre } />
									<ul>
										<li>Libro: { librito.nombre }</li>
										<li>Autor: { librito.autor }</li>
										<li>Categoria: { librito.categoria }</li>
									</ul>
									<p>{ librito.descripcion }</p>
								</div>
						);
					})
				}
			</div>
			
			
			
		</section>
	);
};

export default Test;
